mapper.select:
        SELECT
        

--         级联表的字段

        FROM lajixinxi  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.lajixinxi_name like CONCAT('%',#{params.lajixinxiName},'%')
            
            
                and a.lajixinxi_types = #{params.lajixinxiTypes}
            
            
                  and a.lajixinxi_click_number >= #{params.lajixinxiClickNumberStart}   
            
            
                  and a.lajixinxi_click_number <= #{params.lajixinxiClickNumberEnd}   
            
             
                and a.lajixinxi_click_number = #{params.lajixinxiClickNumber}
             
            
                and a.lajixinxi_content like CONCAT('%',#{params.lajixinxiContent},'%')
            

        

        order by a.${params.orderBy} desc
